def solution(line)
  start_pos = 0
  end_pos = 0
  current_chars = []

  pp line

  while end_pos < (line.length - 1)
    if current_chars.length == 4
      puts end_pos
      return end_pos
    end

    if current_chars.include?(line[end_pos])
      current_chars = []
      start_pos += 1
      end_pos = start_pos
      next
    else
      current_chars << line[end_pos]
      end_pos += 1
    end
  end
end


